        function waterFlow() {
            var num = 2; 
            var arr = []; //定义一个数组
            var imgWidth = $(".z_cen_wntj_box_box").innerWidth();
            for (var j = 0; j < $(".z_cen_wntj_box_box").length; j++) {
                if (j < num) { //一行显示num个div   ,将第一行的每个div高度写入数组
                    arr[j] = $(".z_cen_wntj_box_box").eq(j).innerHeight()
                    // console.log(arr[j])
                } else {
                    var minImgHeight = Math.min.apply(null, arr); //取得数组中最小高度的div
                    var index = getMinIndex(arr, minImgHeight); //取得数组中最小高度的div的索引
                    $(".z_cen_wntj_box_box").eq(j).css({
                        "position": "absolute",
                        "top": minImgHeight + "px",
                        // "left": $(".hotlist").eq(index).offset().left - 63 + "px"
                        "left": imgWidth * index,
                    }); //瀑布流布局
                    arr[index] += $(".z_cen_wntj_box_box").eq(j).innerHeight(); //将布局好的该div的高度和该div上面的div高度相加，重新放入数组
                }
            }
            var maxHeight = Math.max.apply(null, arr);
            $(".z_cen_wntj_box").css({
                "height": maxHeight
            })
        }
        function getMinIndex(arr, min) { //取得数组中最小高度的div的索引
            for (var k in arr) {
                if (arr[k] == min) {
                    return k;
                }
            }
        }
